mapper.select:
select
doc_id, category_id, doc_title, doc_summary, doc_uploaduser, doc_createdate, doc_modifyuser, doc_modifydate,
doc_image
from doc.doc


        and doc_id = #{docId}


        and category_id = #{categoryId}


        and doc_title like concat('%',#{docTitle},'%')


        and doc_summary like concat('%',#{docSummary},'%')


        and doc_uploaduser like concat('%',#{docUploaduser},'%')


        and doc_createdate = #{docCreatedate}


        and doc_modifyuser = #{docModifyuser}


        and doc_modifydate = #{docModifydate}


        and doc_image = #{docImage}
